  `content` longtext CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL COMMENT '文章内容',
  `count_comment` int NULL DEFAULT 0 COMMENT '回复统计',
  `count_view` int NULL DEFAULT 0 COMMENT '帖子展示数',
  `count_love` int NULL DEFAULT 0 COMMENT '喜欢数',
  `count_digg` int NULL DEFAULT 0 COMMENT '支持数',
  `count_burys` int NULL DEFAULT 0 COMMENT '反对次数',
  `count_follow` int NULL DEFAULT 0 COMMENT '关注话题统计数',
  `weight` decimal(11, 0) NULL DEFAULT NULL COMMENT '排序权重',
  `istop` tinyint(1) NULL DEFAULT 0 COMMENT '是否置顶',
  `isclose` int NULL DEFAULT 0 COMMENT '是否关闭帖子',
  `iscomment` tinyint(1) NULL DEFAULT 0 COMMENT '是否允许评论',
