  INDEX `userid`(`user_id` ASC) USING BTREE,
  INDEX `title`(`title` ASC) USING BTREE,
  INDEX `typeid`(`category_id` ASC) USING BTREE,
  INDEX `addtime`(`create_time` ASC) USING BTREE,
  INDEX `count_comment`(`count_comment` ASC) USING BTREE,
  INDEX `count_view`(`count_view` ASC) USING BTREE,
  INDEX `count_love`(`count_love` ASC) USING BTREE,
  INDEX `count_view_2`(`count_view` ASC, `create_time` ASC) USING BTREE,
  INDEX `isshow`(`isaudit` ASC, `update_time` ASC) USING BTREE,
  INDEX `id`(`id` ASC) USING BTREE,
  INDEX `short_url`(`short_url` ASC) USING BTREE
) ENGINE = InnoDB CHARACTER SET = utf8mb3 COLLATE = utf8mb3_general_ci COMMENT = '文章模型' ROW_FORMAT = Dynamic;

